import * as React from 'react';
import { StyleSheet, View, Image, StatusBar, TextInput, Text, AsyncStorage} from 'react-native';
// import Logo from '../assets/images/hand_gift_card.png';
import CommonButton from '../components/CommonButton';

export default function LoginScreen (props) {
    return (
        <View style={styles.container}>
            <StatusBar hidden={true} />

            {/* <Image source={Logo} style={styles.LogoImageContainer}></Image> */}

            <View style={styles.inputContainer}>
                <View style={styles.textInput}>
                    <TextInput style={styles.userInput}
                        placeholder=" Type User Name "
                    >                    
                    </TextInput>
                    <TextInput style={styles.userInput}
                        placeholder=" Type Password "
                    >
                    </TextInput>
                </View>

                <View style={styles.loginButton}>
                <CommonButton
                    icon={'ios-log-in'}
                    iconColor={'#ffdbd9'}
                    iconSize={30}
                    buttonColor={'transparent'}
                    buttonText={'Login'}
                    width={300}
                    height={40}
                    borderColor={'#ffdbd9'}
                    onPress={()=>{props.navigation.navigate('Send', {GiftCardData: ''})}}
                />
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#eb4034',
        alignItems:'center'
    },
    LogoImageContainer: {
        height:100, 
        width:100, 
        alignSelf:'center', 
        resizeMode:'contain'
    },
    inputContainer: {
        flex:1,
        alignItems: 'center',
        flexDirection: 'column',
    },
    userInput: {
        width:300,
        height:30,
        borderBottomColor: '#ffdbd9',
        borderBottomWidth: 2,
        //margin:30
        //backgroundColor:'#dedede',
        //borderRadius:5
    },
    textInput:{
        alignItems: 'center',
        justifyContent:'space-evenly',
        flex:2
    },
    loginButton:{
        flex:1
    }
});
